def get_prompt_by_number(number):
    """Get a specific prompt by number (1-20)"""
    if 1 <= number <= 20:
        return TOP_20_AI_MAGIC_PROMPTS[number - 1]
    else:
        return None

def get_prompts_by_feature(feature):
    """Get all prompts that showcase a specific AI magic feature"""
    matching_prompts = []
    for prompt_data in TOP_20_AI_MAGIC_PROMPTS:
        if feature in prompt_data['ai_magic_features']:
            matching_prompts.append(prompt_data)
    return matching_prompts

